<style lang="scss" scoped>
.appbar {
  position: absolute;
  left: 0;
  right: 0;
  display: flex;

  .navbar-custom {
    display: flex;
    align-items: center;
    padding: 30px 50px;

    @media (max-width: 900px) {
      padding: 20px 13px;
    }

    ul {
      list-style: none;
      display: flex;
      gap: 15px;
      align-items: center;
      padding: 0;
      margin: 0;


      li {
        font-size: 14px;
        font-weight: 500;
      }

      @media (max-width: 1000px) {
        display: none;
      }
    }

    .contacts {
      display: flex;
      align-items: center;
      gap: 14px;
      font-weight: 600;
      font-size: 14px;
      
      @media (max-width: 1330px) {
        button {
          display: none;
        }
      }

      .mobile {
