        @media (max-width: 1000px) {
          display: flex;
          flex-direction: column;
          gap: 5px;

          div {
            width: 30px;
            height: 4px;
            border: 1px solid black;
            border-radius: 3px;
            background: #000;

          }
        }
      }
    }
  }
}
